ideafunnelApp.controller("AllBoardsController",["$scope","$http",function(a,b){a.ideaBoards=[];a.showCreate=false;a.loadAllIdeaBoards=function(){b.post("/api/idea-boards/all",{}).success(function(c){if(c.status&&c.status==="ok"){a.ideaBoards=c.data}else{handleError("Could not load idea boards",c)}}).error(function(c){handleError("An error occurred loading idea boards",c)})};a.viewBoard=function(c){window.location.href="/boards/"+c._id};a.loadAllIdeaBoards()}]);