angular.module("inlineConfirm",[]).directive("inlineConfirm",function(){return{restrict:"EA",scope:{text:"@text",type:"@type",mainClass:"@mainclass",confirmFunction:"&confirmfunction",iconClasses:"@icon",hideOnYes:"@hideOnYes"},template:'<span ng-show="show"><span class="{{mainClass}}" ng-if="type == \'text\'"><i ng-click="showConfirmOptions($event)" ng-if="iconClasses" style="cursor: pointer;" class="{{iconClasses}}"></i><a href="" ng-click="showConfirmOptions()">{{text}}</a> <span ng-show="showConfirm"><span style="margin-left: 5px;"><a href="" ng-click="yesClick($event)">Yes</a></span> <span style="margin-left: 5px;"><a href="" ng-click="noClick($event)">No</a></span></span> </span><button ng-if="type == \'button\'" ng-click="showConfirmOptions()" class="{{mainClass}}"><i ng-if="iconClasses" style="margin-right: 5px; cursor: pointer;" class="{{iconClasses}}"></i> {{text}} <span style="margin-left: 10px;" ng-show="showConfirm"><span ng-click="yesClick($event)">Yes</span> <span style="margin-left: 10px;" ng-click="noClick($event)">No</span></span></button></span>',controller:function(a){a.show=true;a.showConfirmOptions=function(b){a.showConfirm=true;b.preventDefault();b.stopPropagation()};a.noClick=function(b){a.showConfirm=false;b.preventDefault();b.stopPropagation()};a.yesClick=function(b){a.showConfirm=false;a.confirmFunction();if(a.hideOnYes&&a.hideOnYes==="true"){a.show=false}}}}});